document.addEventListener("DOMContentLoaded",function(){if(!getCookie("cookiesAceptadas")){document.getElementById("cookieConsent").style.display="block"}});function setCookie(name,value,days){let expires="";if(days){const date=new Date;date.setTime(date.getTime()+days*24*60*60*1e3);expires="; expires="+date.toUTCString()}document.cookie=name+"="+(value||"")+expires+"; path=/"}function getCookie(name){const nameEQ=name+"=";const ca=document.cookie.split(";");for(let i=0;i<ca.length;i++){let c=ca[i];while(c.charAt(0)==" ")c=c.substring(1,c.length);if(c.indexOf(nameEQ)==0)return c.substring(nameEQ.length,c.length)}return null}function aceptarCookies(){setCookie("cookiesAceptadas","true",365);document.getElementById("cookieConsent").style.display="none"}function rechazarCookies(){setCookie("cookiesAceptadas","false",365);document.getElementById("cookieConsent").style.display="none"}function configurarCookies(){window.location.href="/politica-cookies.html#configuracion"}